import React, { Component } from 'react';
import { Nav, Navbar, NavbarBrand, NavbarToggler, Collapse, NavItem, Jumbotron,
Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Input, Label } from 'reactstrap';
import { NavLink } from 'react-router-dom';
import fire from './fire';

class Header extends Component {
    constructor(props) {
        super(props);
        this.login=this.login.bind(this);
       
        this.toggleNav = this.toggleNav.bind(this);
        this.toggleModal = this.toggleModal.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
       


        this.state = {
          isNavOpen: false,
          isModalOpen: false,
          email:'',
            password:''
        };
      }
  
      login(e) {
        e.preventDefault();
        fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u)=>{
        }).catch((error) => {
            console.log(error);
          });
      }

      toggleNav() {
        this.setState({
          isNavOpen: !this.state.isNavOpen
        });
      }

      toggleModal() {
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });
      }

      handleLogin(event) {
          this.toggleModal();
          alert("Email : " + this.email.value + " Password: " + this.password.value + " Remember: " + this.remember.checked);
          event.preventDefault();
      }
     // handleRegisteration(event) {
     //this.toggleModal();
       // alert("Name : " + this.name.value + "Email : " + this.email.value + " Password: " + this.password.value + " Phone No.(xxxx-xxxxxxx): " + this.phonenum,value);
        //event.preventDefault();
    //}


    render() {
        return(
            <React.Fragment>
                <Navbar dark expand="md">
                    <div className="container">
                        <NavbarToggler onClick={this.toggleNav} />
                        <NavbarBrand className="mr-auto" href="/"><img src='assets/images/logon.png' height="50" width="100" alt='Ristorante Con Fusion' /></NavbarBrand>&nbsp;&nbsp;&nbsp;
                        <Collapse isOpen={this.state.isNavOpen} navbar>
                            <Nav navbar>
                            <NavItem>
                                <NavLink className="nav-link"  to='/home'><span className="link">Home</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link" to='/teletalk'><span className="link">Why Tele Talk?</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/session'><span className="link">Session</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/ebook'><span className="link">eBook/Tutorials</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/activity'><span className="link">Activities</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/team'><span className="link">Team</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link"  to='/mission'><span className="link">Mission</span></NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link" to='/contactus'><span className="link">Contact Us</span></NavLink>
                            </NavItem>
                            </Nav>
                            <Nav className="ml-auto" navbar>
                                <NavItem>
                                    <Button outline onClick={this.toggleModal}>
                                        <span className="fa fa-sign-in fa-lg">Login</span>
                                    </Button>
                                    <Button outline onClick={this.toggleModal}>
                                        <span className="fa fa-sign-in fa-lg">Registration</span>
                                    </Button>
                                </NavItem>
                            </Nav>
                        </Collapse>
                    </div>
                </Navbar>
               
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal} >Login</ModalHeader>
                    <ModalBody>
                    <Form onSubmit={this.handleLogin}>
                            <FormGroup>
                                <label htmlFor="email">Email</label>
                                <Input type="Email" id="email" name="email" innerRef={(input) => this.email = input}/>
                            </FormGroup>
                            <FormGroup>
                                <label htmlFor="password">Password</label>
                                <Input type="password" id="password" name="password" innerRef={(input) => this.password = input}/>
                            </FormGroup>
                            <FormGroup check>
                                <label check> 
                                <Input type="checkbox" name="remember" innerRef={(input) => this.remember = input}/>
                                Remember
                                </label>
                            </FormGroup>
                            <Button type="submit" value="submit" color="primary">Login</Button>
                        </Form>
                        </ModalBody>
                </Modal>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal} >Registeration</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleRegisteration}>
                        <FormGroup>
                                <label htmlFor="username">Name</label>
                                <Input type="name" id="name" name="name" innerRef={(input) => this.email = input}/>
                            </FormGroup>
                            <FormGroup>
                                <label htmlFor="username">Email</label>
                                <Input type="email" id="email" name="email" innerRef={(input) => this.email = input}/>
                            </FormGroup>
                            <FormGroup>
                                <label htmlFor="password">Password</label>
                                <Input type="password" id="password" name="password" innerRef={(input) => this.password = input}/>
                            </FormGroup>
                            <FormGroup>
                                <label htmlFor="password">Phone No.(xxxx-xxxxxxx)</label>
                                <Input type="tel" id="phonenum" name="phonenum" pattern="^\d{4}-\d{7}$" required innerRef={(input) => this.phonenum = input}/>
                            </FormGroup>
                            
                            <Button type="submit" value="submit" color="primary">Register</Button>
                        </Form>
                    </ModalBody>
                </Modal>
                  
            </React.Fragment>
            
        )
    }
}

export default Header;
