import React, { Component } from 'react';
import Menu from './MenuComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import Home from './HomeCompoment';
import contact from './contactComponent';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import teletalk from './whyteletalkcomponent';
import session from './session';
import ebook from './ebookComponent';
import activity from './activityComponent';
import team from './teamComponent';
import mission from './missionComponent';
import Slider from './slider';
import Login from './Login';
import fire from './fire';





class Main extends Component {
constructor(props) {
  super(props);
  this.state={
    authenticated: false
   }

  
}

componentWillMount() {
  this.removeAuthListener = app.auth().onAuthStateChanged((user) => {
    if (user) {
      this.setState({
        authenticated: true,
        currentUser: user,
        loading: false,
      })

      this.songsRef = base.syncState(`songs/${user.uid}`, {
        context: this,
        state: 'songs'
      });
    } else {
      this.setState({
        authenticated: false,
        currentUser: null,
        loading: false,
      })

      base.removeBinding(this.songsRef);
    }
  })
}





  render() {
    const HomePage = () => {
      return(
            <Home />
            
            
        );
       
    }

    return (
      <div>      
        <Header authenticated={this.state.authenticated}/>  
        <Slider/>
        <Switch>
          <Route path="/home" component={HomePage} />
          <Route exact path="/menu" component={Menu} />
          <Route exact path="/contactus" component={contact}/>
          <Route exact path="/teletalk" component={teletalk}/>
          <Route exact path="/session" component={session}/>
          <Route exact path="/ebook" component={ebook}/>
          <Route exact path="/activity" component={activity}/>
          <Route exact path="/team" component={team}/>
          <Route exact path="/mission" component={mission}/>
          <Route exact path="/login" component={Login}/>
          <Redirect to="/home" />

        </Switch>
        <Footer />
      </div>
    );
  }
}

export default Main;
