import React, {Component} from 'react'
import {Redirect} from 'react-router-dom';
import { Toaster, Intent } from '@blueprintjs/core'
import { app, facebookProvider } from './fire'
const loginStyles = {
    width: "90%",
    maxWidth: "315px",
    margin: "20px auto",
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "10px"
  }
class Login extends Component{
    constructor(props) {
        super(props)
        this.authWithFacebook = this.authWithFacebook.bind(this)
        this.authWithEmailPassword = this.authWithEmailPassword.bind(this)
        this.state = {
            redirect: false
          }
    }
    authWithFacebook(){
        console.log("authed with facebook")
    }
    authWithEmailPassword(event) {
        event.preventDefault()
        console.log("authed with email")
        console.table([{
            email:this.emailInput.value,
            password:this.passwordInput.value
        }])
    }
    render(){
        if(this.state.redirect === true){
            return <Redirect to='/'/>
        }
        return(
    <div style={loginStyles} className="container">
     <Toaster ref={(element) => { this.toaster = element }} />
      <button style={{width: "100%"}} className="btn btn-primary" onClick={() => { this.authWithFacebook() }}>Log In with Facebook</button>
        <hr style={{marginTop: "10px", marginBottom: "10px"}}/>
        <form onSubmit={(event) => { this.authWithEmailPassword(event) }} ref={(form) => { this.loginForm = form }}>
          <div style={{marginBottom: "10px"}} className="pt-callout pt-icon-info-sign">
            <h5>Note</h5>
            If you don't have an account already, this form will create your account.
          </div>
          <label className="pt-label">
            Email
            <input style={{width: "113%"}} className="pt-input" name="email" type="email" ref={(input) => { this.emailInput = input }} placeholder="Email"></input>
          </label><br/>
          <label className="pt-label">
            Password
            <input style={{width: "100%"}} className="pt-input" name="password" type="password" ref={(input) => { this.passwordInput = input }} placeholder="Password"></input><br/>
          </label><br/>
          <input style={{width: "100%"}} type="submit" className="btn btn-primary" value="Log In"></input>
        </form>
            </div>
        )
    }
}
export default Login
